import React, { useEffect, useState } from "react";
import NAvbsr from "./NAvbsr";
import photo from "./images/photo.png";
import About from "./About";
import GitHubIcon from "@mui/icons-material/GitHub";
import LinkedInIcon from "@mui/icons-material/LinkedIn";
import FacebookIcon from "@mui/icons-material/Facebook";

function Home() {
  const [widthofscreen, setWidthofscreen] = useState(window.innerWidth);

  useEffect(() => {
    const handleResize = () => {
      setWidthofscreen(window.innerWidth);
    };

    window.addEventListener("resize", handleResize);

    // Cleanup event listener on component unmount
    return () => {
      window.removeEventListener("resize", handleResize);
    };
  }, []);

  return (
    <>
      <NAvbsr />
      <div className="row">
        <div className="col-lg-6 col-md-6 col-sm-6 col-12 intro">
          My self
          <h1>PRITAM MULI</h1>
          <h5>
            System engineer @ TCS | JavaScript | HTML | CSS | ReactJs | React
            Native | Jira | Git
          </h5>
          <div className="row">
            <div className="col-lg-1 col-md-1 col-sm-1 col-1">
              <LinkedInIcon className="contact_icons" />
            </div>
            <div className="col-lg-1 col-md-1 col-sm-1 col-1">
              <FacebookIcon className="contact_icons" />
            </div>
            <div className="col-lg-1 col-md-1 col-sm-1 col-1">
              <GitHubIcon className="contact_icons" />
            </div>
          </div>
        </div>
        <div className="col-lg-6 col-md-6 col-sm-6 col-12 image_section_container">
          {widthofscreen > 576 ? (
            <img src={photo} className="image_section" alt="Profile" />
          ) : null}
        </div>
      </div>
      <About />
    </>
  );
}

export default Home;

